import mathimport scipy.stats as st# CALCULATE Z VALUEdef get_z(confidence_level:float)->float:    """    Calculate Z value for a given confidence level.        confidence_level -- confidence level into percent.     return -- z value.    """    return st.norm.ppf(1-(1-confidence_level/100.)/2)# CALCULATE THE SAMPLE SIZEdef sample_size(population_size:int, confidence_level:float, confidence_interval:float)->int:    """    Calculate the sample size using the Cochranâ€™s Sample Size Formula.        population_size -- the total population size.    confidence_level -- the seleceted confidence level in percent.     confidence_interval -- the selected confidence interval in percent.    return -- sample size with the correction for smaller population (no large).    """    Z = 0.0    p = 0.5    e = confidence_interval/100.0    N = population_size    n_0 = 0.0    n = 0.0    # FIND THE NUM STD DEVIATIONS FOR THAT CONFIDENCE LEVEL    Z = get_z(confidence_level)    if Z == 0.0:        return -1    # CALC SAMPLE SIZE    n_0 = ((Z**2) * p * (1-p)) / (e**2)    # ADJUST SAMPLE SIZE FOR FINITE POPULATION    n = n_0 / (1 + ((n_0 - 1) / float(N)) )    return int(math.ceil(n)) # THE SAMPLE SIZE