import pandas as pdclass Columns():        # constructor    def __init__(self, df:pd.DataFrame):        """        Constructor of Columns class to detect types of variables for each column.        Parameters        ----------        df : pd.DataFrame            Dataframe to be used.        Returns        -------        None.        """        self.num = df.select_dtypes(include=['float64']).columns.values  # numerical columns        self.ord = df.select_dtypes(include=['int64']).columns.values    # numerical columns        self.cat = df.select_dtypes(include=['object']).columns.values   # categorical columns              # printer    def __str__(self):        """        Printer.        Returns        -------        str            String to be printed.        """        return f'Categorical: {self.cat} \nNumerical: {self.num} \nOrdinal: {self.ord}' class Report():        # constructor    def __init__(self):        self.records = list()            # add new record    def add(self, new_record:'str or list'):        # validate if is a str or list        if type(new_record) == str:            new_record = [new_record]        # add new record        self.records += new_record            # display records    def display(self, max_records:int = None):        for i, record in enumerate(self.records):            # display record            print(f'[{i}] {record}')            # stop display            if not max_records is None and i == max_records:                break        # count number of records    def count(self):        # display        print(f'Number of records = {len(self.records)}')